import z from"zod";const passwordRegex=/(?=.*[A-Z])(?=.*[a-z])(?=.*\d).+$/,passwordError="Password must contain at least one uppercase letter, one lowercase letter, and one number.";export const FormSchema=z.object({email:z.string().min(1,"Email is required").email("Please enter a valid email address"),firstName:z.string().min(1,"First name is required"),lastName:z.string().min(1,"Last name is required"),username:z.string().min(3,"Username must be at least 3 characters long"),password:z.string().min(8,"Password must be at least 8 characters long").regex(passwordRegex,passwordError),repeat_password:z.string().min(1,"Please confirm your password")});export const ConfirmSchema=FormSchema.refine((e=>e.password===e.repeat_password),{message:"Passwords do not match",path:["repeat_password"]});export function getFieldError(e,r){const{error:s}=FormSchema.shape[e].safeParse(r);return s?s.issues.map((e=>e.message)).join(", "):void 0}export const getErrors=e=>e.issues.reduce(((e,r)=>{const s=r.path.join(""),a=e[s]?e[s]+", ":"";return e[s]=a+r.message,e}),{});